import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

function QuizStart() {
  const [level, setLevel] = useState("");
  const navigate = useNavigate();

  const handleContinue = () => {
    if (!level) {
      alert("Please select your education level");
      return;
    }

    if (level === "10th") navigate("/quiz/10th");
    if (level === "intermediate") navigate("/quiz/intermediate");
    if (level === "college") navigate("/quiz/college");
  };

  return (
    <div className="quiz-container">
      <h3>Select Your Education Level</h3>

      <label>
        <input type="radio" name="level" value="10th"
          onChange={(e) => setLevel(e.target.value)} />
        10th Standard
      </label>

      <label>
        <input type="radio" name="level" value="intermediate"
          onChange={(e) => setLevel(e.target.value)} />
        Intermediate (11â€“12)
      </label>

      <label>
        <input type="radio" name="level" value="college"
          onChange={(e) => setLevel(e.target.value)} />
        B.Tech / College
      </label>

      <button onClick={handleContinue}>Continue</button>
    </div>
  );
}

export default QuizStart;
