import { Outlet } from "react-router-dom";
import "./About.css"; // footer styles already here

function Layout() {
  return (
    <>
      {/* BLUE HEADER (unchanged) */}
      <header className="top-bar">
        <div className="logo">Career and Education Advisor</div>

        <nav>
          <button
            className="link-btn"
            onClick={() => setOpenIntitutionInfo(prev => !prev)}
          >
            Institutions
          </button>

          <button
            className="link-btn"
            onClick={() => setOpenResourceInfo(prev => !prev)}
          >
            Resources
          </button>

          {/* ABOUT US NAVIGATION */}
          <Link to="/about" className="link-btn">
            About us
          </Link>

          {/* REGISTER */}
          <Link to="/register" className="nav-register-btn">
            Register
          </Link>
        </nav>

        {openIntitutionInfo && <IntitutionInfo />}
        {openResourceInfo && <ResourceInfo />}
      </header>

      {/* PAGE CONTENT CHANGES HERE */}
      <Outlet />

      {/* BLACK FOOTER (unchanged) */}
      <footer className="footer">
        <div className="footer-left">
          <p><strong>Contact us:</strong></p>
          <p>Email: support@careereducationadvisor.com</p>
          <p>Phone: +91-8309214468</p>
        </div>

        <div className="footer-center">
          <p>For guidance purposes only</p>
          <p>Â© 2025 Career & Education Advisor | Academic Project | All Rights Reserved</p>
        </div>
      </footer>
    </>
  );
}

export default Layout;
