import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./styles.css";
import IntitutionInfo from "./components/InstitutionInfo";
import ResourceInfo from "./components/ResourceInfo";

export default function Home() {
  const [openIntitutionInfo, setOpenIntitutionInfo] = useState(false);
  const [openResourceInfo, setOpenResourceInfo] = useState(false);

  return (
    <div className="home-page">
      {/* TOP BAR */}
      <header className="top-bar">
        <div className="logo">Career and Education Advisor</div>

        <nav>
          <button
            className="link-btn"
            onClick={() => setOpenIntitutionInfo(prev => !prev)}
          >
            Institutions
          </button>

          <button
            className="link-btn"
            onClick={() => setOpenResourceInfo(prev => !prev)}
          >
            Resources
          </button>

          <Link to="/about" className="link-btn">
            About us
          </Link>

          <Link to="/register" className="nav-register-btn">
            Register
          </Link>
        </nav>

        {openIntitutionInfo && <IntitutionInfo />}
        {openResourceInfo && <ResourceInfo />}
      </header>

      {/* MAIN CONTENT */}
      <main className="home-content">
        <h1>One-Stop Personalized Career & Education Advisor</h1>
        <p>
          A smart platform that helps students choose the right career path
          by analyzing interests, skills, and academics.
        </p>

        <button className="primary-btn">START QUIZ</button>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-left">
          <p><strong>Contact us:</strong></p>
          <p>Email: support@careeradvisor.com</p>
          <p>Phone: +91-8309214468</p>
        </div>

        <div className="footer-right">
          <p>For guidance purposes only</p>
          <p>Â© 2025 Career & Education Advisor | Academic Project</p>
        </div>
      </footer>
    </div>
  );
}
