import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

function QuizStart() {
  const [level, setLevel] = useState("");
  const navigate = useNavigate();

  const handleContinue = () => {
    if (!level) {
      alert("Please select your education level");
      return;
    }

    // For now only 10th standard flow
    if (level === "10th") {
      navigate("/quiz/10th");
    }
  };

  return (
    <div className="quiz-container">
      <h3>Select Your Education Level</h3>

      <div className="radio-group">
        <label>
          <input type="radio" name="level" value="10th" onChange={(e) => setLevel(e.target.value)} />
          10th Standard
        </label>

        <label>
          <input type="radio" name="level" value="intermediate" />
          Intermediate (11thâ€“12th)
        </label>

        <label>
          <input type="radio" name="level" value="college" />
          B.Tech / College
        </label>
      </div>

      <button className="continue-btn" onClick={handleContinue}>
        Continue
      </button>
    </div>
  );
}

export default QuizStart;
