import { useState } from "react";
import quizQuestions10th from "./quizQuestions10th";
import "./styles.css";

function Quiz10th() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);

  const questionData = quizQuestions10th[currentQuestion];
  const totalQuestions = quizQuestions10th.length; // ✅ FIX

  const handleOptionClick = (option) => {
    const updatedAnswers = [...answers];
    updatedAnswers[currentQuestion] = option;
    setAnswers(updatedAnswers);
  };

  const handleNext = () => {
    if (!answers[currentQuestion]) {
      alert("Please select an option");
      return;
    }

    if (currentQuestion < totalQuestions - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert("Quiz completed!");
      console.log("Final Answers:", answers);
    }
  };

  return (
    <div className="quiz-page">
      {/* QUESTION BOX */}
      <div className="quiz-container">
        <h4>{questionData.question}</h4>

        <div className="options-grid">
          {questionData.options.map((option, index) => (
            <button
              key={index}
              className={`option-btn ${
                answers[currentQuestion] === option ? "selected" : ""
              }`}
              onClick={() => handleOptionClick(option)}
            >
              {option}
            </button>
          ))}
        </div>

        {/* NEXT BUTTON INSIDE BOX */}
        <div className="quiz-actions">
          <button className="next-btn" onClick={handleNext}>
            {currentQuestion === totalQuestions - 1 ? "Finish" : "Next"}
          </button>
        </div>
      </div>

      {/* QUESTION COUNT — OUTSIDE BOX & CENTERED */}
      <div className="question-count">
        Question {currentQuestion + 1} of {totalQuestions}
      </div>
    </div>
  );
}

export default Quiz10th;
