import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminHeader.css";
import logo from "../logo.svg";
import AdminSidebar from "./AdminSidebar";

const AdminHeader = () => {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false); // âœ… sidebar state

  const logout = () => {
    localStorage.clear();
    navigate("/");
  };

  return (
    <>
      <header className="admin-header">
        <div className="admin-left">
          {/* â˜° MENU ICON */}
          <span className="menu-icon" onClick={() => setOpen(true)}>
            â˜°
          </span>

          <img src={logo} alt="logo" className="logo" />
          <span className="title">Career and Education Advisor</span>
        </div>

        <div className="admin-right">
          <span
            className="profile-icon clickable"
            title="Profile"
            onClick={() => navigate("/profile")}
          >
            ðŸ‘¤
          </span>

          <button onClick={logout}>Logout</button>
        </div>
      </header>

      {/* âœ… ADMIN SIDEBAR */}
      <AdminSidebar isOpen={open} onClose={() => setOpen(false)} />
    </>
  );
};

export default AdminHeader;
