import { Outlet, useLocation } from "react-router-dom";
import HeaderBeforeLogin from "./components/HeaderBeforeLogin";
import HeaderAfterLogin from "./components/HeaderAfterLogin";
import AdminHeader from "./components/AdminHeader";
import "./components/Footer.css";

function Layout() {
  const location = useLocation();

  const isAuthenticated =
    localStorage.getItem("isAuthenticated") === "true";
  const userRole = localStorage.getItem("userRole");

  const isAuthPage =
    location.pathname === "/login" ||
    location.pathname === "/register";

  const isAdminPage = location.pathname.startsWith("/admin");

  return (
    <div className="page-wrapper">
      {/* ================= HEADERS ================= */}

      {/* ADMIN HEADER */}
      {isAuthenticated && userRole === "admin" && isAdminPage && (
        <AdminHeader />
      )}

      {/* STUDENT / BEFORE LOGIN HEADERS */}
      {!isAuthPage && !isAdminPage && (
        <>
          {!isAuthenticated && <HeaderBeforeLogin />}
          {isAuthenticated && userRole === "student" && (
            <HeaderAfterLogin />
          )}
        </>
      )}

      {/* ================= PAGE CONTENT ================= */}
      <main className="page-content">
        <Outlet />
      </main>

      {/* ================= FOOTER ================= */}
      {!isAuthPage && !isAdminPage && <Footer />}
    </div>
  );
}

function Footer() {
  return (
    <footer className="footer">
      <div className="footer-left">
        <p><strong>Contact us:</strong></p>
        <p>Email: support@careeredvisor.com</p>
        <p>Phone: +91-8309214468</p>
      </div>

      <div className="footer-right">
        <p className="guidance-text">For guidance purposes only</p>
        <p className="copyright-text">
          Â© 2025 Career & Education Advisor | Academic Project | All Rights Reserved
        </p>
      </div>
    </footer>
  );
}

export default Layout;
