import { useState } from "react";
import AdminHeader from "./components/AdminHeader";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  /* ================== STATES ================== */

  const [career, setCareer] = useState({
    name: "",
    skills: "",
    category: "",
    salary: ""
  });

  const [course, setCourse] = useState({
    name: "",
    skills: "",
    link: "",
    platform: ""
  });

  const [college, setCollege] = useState({
    name: "",
    location: "",
    courses: ""
  });

  const [quiz, setQuiz] = useState({
    question: "",
    options: "",
    mapping: ""
  });

  /* ================== HANDLERS ================== */

  const handleCareerChange = (e) => {
    setCareer({ ...career, [e.target.name]: e.target.value });
  };

  const handleCourseChange = (e) => {
    setCourse({ ...course, [e.target.name]: e.target.value });
  };

  const handleCollegeChange = (e) => {
    setCollege({ ...college, [e.target.name]: e.target.value });
  };

  const handleQuizChange = (e) => {
    setQuiz({ ...quiz, [e.target.name]: e.target.value });
  };

  /* ================== ACTIONS ================== */

  const addCareer = () => {
    console.log("Career Added:", career);
    alert("Career added successfully");
    setCareer({ name: "", skills: "", category: "", salary: "" });
  };

  const editCareer = () => {
    console.log("Career Edited:", career);
    alert("Career updated");
  };

  const deleteCareer = () => {
    console.log("Career Deleted:", career);
    alert("Career deleted");
    setCareer({ name: "", skills: "", category: "", salary: "" });
  };

  const addCourse = () => {
    console.log("Course Added:", course);
    alert("Course added successfully");
    setCourse({ name: "", skills: "", link: "", platform: "" });
  };

  const addCollege = () => {
    console.log("College Added:", college);
    alert("College added successfully");
    setCollege({ name: "", location: "", courses: "" });
  };

  const addQuiz = () => {
    console.log("Quiz Added:", quiz);
    alert("Quiz question added");
    setQuiz({ question: "", options: "", mapping: "" });
  };

  /* ================== UI ================== */

  return (
    <>
      <AdminHeader />

      <div className="admin-body">
        <h3>Dashboard Overview</h3>

        {/* OVERVIEW */}
        <div className="stats">
          <div><b>130</b><br />Users</div>
          <div><b>67</b><br />Career Options</div>
          <div><b>45</b><br />Courses</div>
          <div><b>39</b><br />Colleges</div>
        </div>

        {/* MANAGE CAREERS */}
        <section className="admin-card careers-card">
          <h4 className="card-title">Manage Careers</h4>
          <div className="card-content">
            <div className="form-left">
              <div className="form-row">
                <label>Career Name:</label>
                <input name="name" value={career.name} onChange={handleCareerChange} />
              </div>
              <div className="form-row">
                <label>Required Skills:</label>
                <input name="skills" value={career.skills} onChange={handleCareerChange} />
              </div>
              <div className="form-row">
                <label>Category:</label>
                <input name="category" value={career.category} onChange={handleCareerChange} />
              </div>
              <div className="form-row">
                <label>Salary Range:</label>
                <input name="salary" value={career.salary} onChange={handleCareerChange} />
              </div>
            </div>

            <div className="form-actions">
              <button className="btn-edit" onClick={editCareer}>Edit</button>
              <button className="btn-delete" onClick={deleteCareer}>Delete</button>
              <button className="btn-add" onClick={addCareer}>+ Add Career</button>
            </div>
          </div>
        </section>

        {/* MANAGE COURSES */}
        <section className="admin-card courses-card">
          <h4 className="card-title">Manage Courses</h4>
          <div className="card-content">
            <div className="form-left">
              <div className="form-row">
                <label>Course Name:</label>
                <input name="name" value={course.name} onChange={handleCourseChange} />
              </div>
              <div className="form-row">
                <label>Skills:</label>
                <input name="skills" value={course.skills} onChange={handleCourseChange} />
              </div>
              <div className="form-row">
                <label>Link:</label>
                <input name="link" value={course.link} onChange={handleCourseChange} />
              </div>
              <div className="form-row">
                <label>Platform:</label>
                <input name="platform" value={course.platform} onChange={handleCourseChange} />
              </div>
            </div>

            <div className="form-actions">
              <button className="btn-add" onClick={addCourse}>+ Add Course</button>
            </div>
          </div>
        </section>

        {/* MANAGE COLLEGES */}
        <section className="admin-card colleges-card">
          <h4 className="card-title">Manage Colleges / Institutions</h4>
          <div className="card-content">
            <div className="form-left">
              <div className="form-row">
                <label>College Name:</label>
                <input name="name" value={college.name} onChange={handleCollegeChange} />
              </div>
              <div className="form-row">
                <label>Location:</label>
                <input name="location" value={college.location} onChange={handleCollegeChange} />
              </div>
              <div className="form-row">
                <label>Courses Offered:</label>
                <input name="courses" value={college.courses} onChange={handleCollegeChange} />
              </div>
            </div>

            <div className="form-actions">
              <button className="btn-add" onClick={addCollege}>+ Add College</button>
            </div>
          </div>
        </section>

        {/* MANAGE QUIZ */}
        <section className="admin-card quiz-card">
          <h4 className="card-title">Manage Quiz Questions</h4>
          <div className="card-content">
            <div className="form-left">
              <div className="form-row">
                <label>Question:</label>
                <input name="question" value={quiz.question} onChange={handleQuizChange} />
              </div>
              <div className="form-row">
                <label>Options:</label>
                <input name="options" value={quiz.options} onChange={handleQuizChange} />
              </div>
              <div className="form-row">
                <label>Mapping:</label>
                <input name="mapping" value={quiz.mapping} onChange={handleQuizChange} />
              </div>
            </div>

            <div className="form-actions">
  <button className="btn-edit">Edit</button>
  <button className="btn-delete">Delete</button>
  <button className="btn-add">+ Add Question</button>
</div>
          </div>
        </section>
      </div>
    </>
  );
};

export default AdminDashboard;
