import { Link, useNavigate } from "react-router-dom";
import "./Header.css";

function HeaderAfterLogin() {
  const navigate = useNavigate();
  const [showInstitution, setShowInstitution] = useState(false);
  const [showResource, setShowResource] = useState(false);

  // ‚úÖ close all popups
  const closePopups = () => {
    setShowInstitution(false);
    setShowResource(false);
  };

  const handleLogout = () => {
    // 1Ô∏è‚É£ Clear auth data
    localStorage.removeItem("isAuthenticated");
    localStorage.removeItem("userRole");

    // 2Ô∏è‚É£ Navigate to first page
    navigate("/", { replace: true });

    // 3Ô∏è‚É£ Force React to re-evaluate auth state
    window.location.reload(); // ‚úÖ IMPORTANT
  };

  return (
    <header className="header">
      <div className="header-left">
        <div className="menu-icon">
          <span></span>
          <span></span>
          <span></span>
        </div>

        <img src="/logo.png" alt="Logo" className="logo-img" />
        <span className="logo-text">Career and Education Advisor</span>
      </div>

      <nav>
        <Link to="/">Home</Link>
        <Link to="/institutions">Institutions</Link>
        <Link to="/resources">Resources</Link>
        <Link to="/about">About us</Link>

        <span className="profile-icon">üë§</span>

        <button onClick={handleLogout} className="btn-logout">
          Logout
        </button>
      </nav>
    </header>
  );
}

export default HeaderAfterLogin;
